import glob
import re
from PIL import Image
from lib.image.process import Process

class Compare:
    def __init__(self):
        self.data = []
        self.dt = [
            {
                'path': '0',
                'chain': ''
            },
            {
                'path': '1',
                'chain': ''
            },
            {
                'path': '2',
                'chain': ''
            },
            {
                'path': '3',
                'chain': ''
            },
            {
                'path': '4',
                'chain': ''
            },
            {
                'path': '5',
                'chain': ''
            },
            {
                'path': '6',
                'chain': ''
            },
            {
                'path': '7',
                'chain': ''
            },
            {
                'path': '8',
                'chain': ''
            },
            {
                'path': '9',
                'chain': ''
            }
        ]
        # print(Compare.get_chain(Process().thresholding(Image.open('6.jpg'), False)))
        # print(self.get_best_find(self.get_chain(Image.open('base/6.jpg'))))

    @staticmethod
    def get_chain(image: Image) -> str:
        chain = ''
        for color in image.getdata():
            chain += '1' if color else '0'
        return chain

    def get_best_find(self, find: str) -> str:
        misses = {image['path']: 0 for image in self.data}
        for image in self.data:
            path = image['path']
            chain = image['chain']
            if find == chain:
                return path
            misses[path] = self.get_hamming_distance(find, chain)
        minimum = min(misses.values())
        for path, miss in misses.items():
            if miss == minimum:
                return path

    def get_best_find_2(self, find: str) -> str:
        misses = {image['path']: 0 for image in self.dt}
        for image in self.dt:
            path = image['path']
            chain = image['chain']
            if find == chain:
                return path
            misses[path] = self.get_hamming_distance(find, chain)
        minimum = min(misses.values())
        for path, miss in misses.items():
            if miss == minimum:
                return path

    @staticmethod
    def get_hamming_distance(first: str, second: str) -> int:
        miss = 0
        for i in range(min(len(first), len(second))):
            if first[i] != second[i]:
                miss += 1
        return miss + abs(len(first) - len(second))

    def load_font(self, directory: str = 'base'):
        sym_regex = re.compile(r'\\(.+)\.jpg')
        sym_aliases = {
            'амперсанд': '&',
            'воскл': '!',
            'двоеточие': ':',
            'дефис': '-',
            'доллар': '$',
            'запятая': ',',
            'звёздочка': '*',
            'карета': 'ˆ',
            'плюс': '+',
            'влево': '<',
            'вправолол': '>',
            'закрытая': '}',
            'тильда': '~',
            'открытая': '{',
            'палка': '|',
            'процент': '%',
            'равно': '=',
            'решётка': '#',
            'собака': '@',
            'вопрос': '?',
            'точка': '.',
            'циркумфлекс': '^',
            'откр': '(',
            'закр': ')',
            'точка с запятой': ';',
            'скобка1': '[',
            'скобка': ']',
        }
        proc = Process()
        for file in glob.glob(directory + '/*.jpg'):
            sym = sym_regex.findall(file)[0]
            if sym in sym_aliases:
                sym = sym_aliases[sym]
            self.data.append({
                'path': sym,
                'chain': Compare.get_chain(proc.thresholding(Image.open(file), False))
            })

    def get_similar(self, miss: float = 0.2):
        result = {}
        miss = 50 * 60 * miss
        for x in range(len(self.data) - 1):
            first_image = self.data[x]
            first_chain = first_image['chain']
            for y in range(x + 1, len(self.data)):
                second_image = self.data[y]
                second_chain = second_image['chain']
                best_miss = 50 * 60
                current_miss = 0
                for i in range(50 * 60):
                    if first_chain[i] != second_chain[i]:
                        current_miss += 1
                if current_miss < best_miss:
                    best_miss = current_miss
                if best_miss < miss:
                    if first_image['path'] in result:
                        result[first_image['path']].append(second_image['path'])
                    else:
                        result[first_image['path']] = [second_image['path']]
                    break
        return result
